import React from "react";
import {
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Typography,
  IconButton,
} from "@material-ui/core";
import { AddShoppingCart } from "@material-ui/icons";
import axios from "axios";

import useStyles from "./style";

function Product({ product }) {
  const classes = useStyles();

  const url = "http://localhost:8080/api/cart/addInCart/";
  const quantity = 0;
  var bodyFormData = new FormData;
  bodyFormData.append("userId",localStorage.getItem("id"))
  bodyFormData.append("product_id",product.id)

  const addToCart = (product) => {
    console.log(localStorage.getItem("id") + "s");
    console.log(product.id);


    axios
      .post({
          method:"post",
          ur: url,
          data: bodyFormData,
      })
      .then(() => {
        localStorage.setItem("quantity", quantity + 1);
        console.log("ssssss");
      });
  };

  return (
    <Card className={classes.root}>
      <CardMedia
        className={classes.media}
        image={product.image}
        title={product.name}
      />
      <CardContent>
        <div className={classes.cardContent}>
          <Typography variant="h5" gutterBottom>
            {product.name}
          </Typography>
          <Typography>{product.price + " $"}</Typography>
        </div>
        <Typography variant="body2" color="textSecondary">
          {" "}
          {product.description}
        </Typography>
      </CardContent>
      <CardActions disableSpacing className={classes.cardActions}>
        <IconButton aria-label="Add to Cart">
          <AddShoppingCart onClick={addToCart(product)} />
        </IconButton>
      </CardActions>
    </Card>
  );
}

export default Product;
