import React, { useState, useEffect } from "react";
import {
  AppBar,
  Toolbar,
  IconButton,
  Badge,
  MenuItem,
  Menu,
  Typography,
  Input,
} from "@material-ui/core";
import axios from 'axios';
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";
import { CallMissedSharp, ShoppingCart } from "@material-ui/icons";
import Modal from "@mui/material/Modal";
import TextField from "@mui/material/TextField";

import logo from "../../assets/logo.png";
import useStyles from "./styles";

function Navbar() {
  const classes = useStyles();

  const [open, setOpen] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginMessage, setLoginMessage] = useState("");
  const [loginStatus, setLoginStatus]= useState("");
  const url = "http://localhost:8080/api/auth/signin";
  const updateUsername = (e) => setUsername(e.target.value);
  const updatePassword = (e) => setPassword(e.target.value);

  const handleOpenModal = () => {setModalVIsibile(true)}
  const handleCloseModal = () => {setModalVIsibile(false)}


  const login= () =>{

    axios.post(url,{
       username: username,
       password: password
    }).then((response) => {
      localStorage.setItem("user", JSON.stringify(response.data.username))
      localStorage.setItem("token", JSON.stringify(response.data.accessToken))

      if(!response.data.message) {
        setLoginStatus(false);
        setModalVIsibile(false)
      } else {
        setLoginStatus(response.data[0].username)


      }

    });
  }

  const style = {
    position: "absolute",
    top: "30%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: "80%",
    maxWidth: "400px",
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };
  return (
    <>
      <AppBar position="fixed" className={classes.appBar} color="inherit">
        <Toolbar>
          <Typography variant="h6" className={classes.title}>
            <img
              src={logo}
              alt="Electronic.js"
              height="25px"
              className={classes.image}
            />
            Electronic store
          </Typography>
          <div className={classes.grow} />

          <div className={classes.button}>
            <IconButton aria-label="Show cart items" color="inherit">
              <Badge badgeContent={2} color="secondary"></Badge>
              <ShoppingCart />
            </IconButton>
          </div>
          <div onClick={handleOpenModal} className={classes.loginButton}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="22"
              height="22"
              fill="currentColor"
              class="bi bi-box-arrow-right"
              viewBox="0 0 16 16"
            >
              <path
                fill-rule="evenodd"
                d="M10 12.5a.5.5 0 0 1-.5.5h-8a.5.5 0 0 1-.5-.5v-9a.5.5 0 0 1 .5-.5h8a.5.5 0 0 1 .5.5v2a.5.5 0 0 0 1 0v-2A1.5 1.5 0 0 0 9.5 2h-8A1.5 1.5 0 0 0 0 3.5v9A1.5 1.5 0 0 0 1.5 14h8a1.5 1.5 0 0 0 1.5-1.5v-2a.5.5 0 0 0-1 0v2z"
              />
              <path
                fill-rule="evenodd"
                d="M15.854 8.354a.5.5 0 0 0 0-.708l-3-3a.5.5 0 0 0-.708.708L14.293 7.5H5.5a.5.5 0 0 0 0 1h8.793l-2.147 2.146a.5.5 0 0 0 .708.708l3-3z"
              />
            </svg>
          </div>
          <div className={classes.logoutButton}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="22"
              height="22"
              fill="currentColor"
              class="bi bi-door-open"
              viewBox="0 0 16 16"
            >
              <path d="M8.5 10c-.276 0-.5-.448-.5-1s.224-1 .5-1 .5.448.5 1-.224 1-.5 1z" />
              <path d="M10.828.122A.5.5 0 0 1 11 .5V1h.5A1.5 1.5 0 0 1 13 2.5V15h1.5a.5.5 0 0 1 0 1h-13a.5.5 0 0 1 0-1H3V1.5a.5.5 0 0 1 .43-.495l7-1a.5.5 0 0 1 .398.117zM11.5 2H11v13h1V2.5a.5.5 0 0 0-.5-.5zM4 1.934V15h6V1.077l-6 .857z" />
            </svg>
          </div>
          <Modal
            visible={isModalVisible}
            open={() => setModalVIsibile(true)}
            onClose = {()=> setModalVIsibile(false)}
            aria-labelledby="modal-modalLogIn"
            aria-describedby="modal-descriere"
          >
            <Box sx={style}>
              <Box
                style={{
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "center",
                  alignItems: "center",
                }}
                component="form"
                sx={{ "&.MuiTextField-root": { m: 1, width: "25ch" } }}
                noValidate
                autocomplete="off"
              >
                <input onChange= {updateUsername}
                  required
                  value={username}
                  id="username"
                  placeholder="Enter your username"
                  size="small"
                />
                <input onChange= {updatePassword}
                  required
                  value={password}
                  id="password"
                  label="Password"
                  type="password"
                  placeholder="Enter your password"
                  size="small"
                />
              </Box>
              <Stack
                style={{
                  display: "flex",
                  alignItems: "center",
                  margin: "30px",
                  justifyContent: "center",
                }}
                spacing={2}
                direction="row"
              >
                <button onClick ={login} type = 'submit' >Log In</button>
                <button variant="text" color="error">
                  Cancel
                </button>
              </Stack>
            </Box>
          </Modal>
        </Toolbar>
      </AppBar>
    </>
  );
}

export default Navbar;
