import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import {
  Products,
  Navbar,
  Cart,
  Checkout,
  Pagination,
} from "./components";
import ReactPaginate from 'react-paginate'
import Footer, { FooterContainer } from "./components/footer/Footer";

const App = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [cartItems, setCartItems] = useState([]);
  const [pageCount, setpageCount] = useState(0);
  const [data, setData]= useState([]);
  let limit = 5;

  const onRemove = (product) => {
    const exist = cartItems?.find((x) => x.id === product.id);

    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== product.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };

  const onAdd = (product) => {
    const exist = cartItems?.find((x) => x.id === product.id);

    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty + 1 } : x
        )
      );
      // localStorage.setItem("cartItems",JSON.stringify(cartItems))

      // JSON.parse(localStorage.getItem("cartItems"))
    } else {
      setCartItems([...cartItems, { ...product, qty: 1 }]);
    }
  };

  useEffect(()=> {
    const fetchProducts = async () => {

      const res =  await fetch('http://localhost:8080/api/products?page=0&limit=5')
        const data = await res.json();
        setData(data)
        const total = data.totalElements;
        setpageCount(Math.ceil(total / limit));
        setProducts(data.content);



    };

    fetchProducts();

  },[limit])

  const fetchProducts = async (page) => {

    const res =  await fetch('http://localhost:8080/api/products?page='+page+'&limit=5')
      const data = await res.json();
      return data.content;
  };

  const handlePageClick = async (data) => {
    let page = data.selected ;
    const commentsFormServer = await fetchProducts(page);

    setProducts(commentsFormServer);
  }

  const onRemoveProduct = (product) => {
    const exist = cartItems?.find((x) => x.id === product.id);
    if (exist) {
      setCartItems(cartItems.filter((x) => x.id !== product.id));
    }
  };

  const onEmptyCart = () => {
    cartItems.forEach((element) => {
      setCartItems(cartItems.filter((element) => element.id !== element.id));
    });
  };



  // const fetchCart =async () => {
  //     const cart = await fetch("http://localhost:8080/api/cart/getProducts?cart_id="+localStorage.getItem("id"))
  //     .then(response=> response.json())
  //     .then(receiveData => setCart(receiveData))
  //     .then(receiveData=> console.log(receiveData));

  // }



  useEffect(() => {
    if (cartItems.length !== 0) {
      localStorage.setItem("cartItems", JSON.stringify(cartItems));
    }
  }, [cartItems]);

  return (
    <Router>
      <div>
        <Navbar onAdd={onAdd} cartItems={cartItems} />
        <Switch>
          <Route exact path="/">
            <Products onAdd={onAdd} products={products} />
          </Route>
          <Route exact path="/cart">
            <Cart
              onAdd={onAdd}
              onRemove={onRemove}
              cartItems={cartItems}
              onRemoveProduct={onRemoveProduct}
              onEmptyCart={onEmptyCart}
            />
          </Route>
          <Route exact path="/checkout">
            <Checkout cartItems={cartItems} />
          </Route>
        </Switch>
      </div>
      <div>
      <Switch>
      <Route exact path="/">
        <Pagination totalProducts= {data.totalElements} pageCount = {data.totalPages}  handlePageClick= {handlePageClick}/>
        </Route>


          <>
          <FooterContainer/>
          </>

        </Switch>   
      </div>
    </Router>
  );
};

export default App;
