import React from "react";
import { Typography, Button, Divider } from "@material-ui/core";
import {
  Elements,
  CardElement,
  ElementsConsumer,
  CardElementContainer,
} from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import Review from "./Review";

const PaymentForm = (props) => {


  const { cartItems, backStep , shippingData, nextStep} = props;

  const stripePromise = loadStripe(process.env.REACT_APP_STRIPE);
  console.log(stripePromise);
  const handleSubmit= async (event, elements, stripe) => {
        event.preventDefault();

        if(!stripe || !elements) return ;

        const cardElement= elements.getElement(CardElement);

        const { error, paymentMethod } = await stripe.createPaymentMethod({ type: 'card', card: cardElement });

        if(error) {
            console.log(error)
        } else {
            const orderData = {
                line_items : cartItems,
                customer: {firstname: shippingData.firstname, lastname : shippingData.lastname, email: shippingData.email,
                adress: shippingData.adress, city: shippingData.city, country: shippingData.shippingCountry},
                payment: {
                    gateway: 'stripe', stripe: {
                        payment_method_id: paymentMethod.id,
                    },
                },
            };
            nextStep();


        }

  }

  return (
    <>

      <Review cartItems={cartItems} />
      <Divider />
      <Typography variant="h6" gutterBottom style={{ margin: "20px 0" }}>
        Payment method
      </Typography>
      <Elements stripe={stripePromise}>
        <ElementsConsumer stripe={stripePromise}>
          {({ elements, stripe }) => (
            <form onSubmit= {(e)=> handleSubmit(e, elements, stripe)}>
                <CardElement />
              <br /> <br />
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <Button variant="outlined" onClick={backStep}>
                  Back
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  disable={!stripe}
                  color="primary"
                >
                  Pay
                </Button>
              </div>
            </form>
          )}
        </ElementsConsumer>
      </Elements>
    </>
  );
};

export default PaymentForm;
