import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { Products, Navbar, Cart } from "./components";



const App = () => {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);



  const onAdd = (product) => {
    const exist = cartItems?.find((x) => x.id === product.id);

    if (exist) {

      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty + 1 } : x
        ) 
      );
      // localStorage.setItem("cartItems",JSON.stringify(cartItems))

      // JSON.parse(localStorage.getItem("cartItems"))

    } else {
      setCartItems([...cartItems, { ...product, qty: 1 }],

        );

        // localStorage.setItem("cartItems",JSON.stringify(cartItems))


    }



  };

  useEffect(()=> {
    if(cartItems.length!= 0) {
      localStorage.setItem("cartItems",JSON.stringify(cartItems))
    }

  },[cartItems])




  const fetchProducts = async () => {
    await fetch("http://localhost:8080/api/products")
      .then((response) => response.json())
      .then((receiveData) => setProducts(receiveData));
  };
  // const fetchCart =async () => {
  //     const cart = await fetch("http://localhost:8080/api/cart/getProducts?cart_id="+localStorage.getItem("id"))
  //     .then(response=> response.json())
  //     .then(receiveData => setCart(receiveData))
  //     .then(receiveData=> console.log(receiveData));

  // }



  useEffect(() => {
    fetchProducts();
  }, []);

  // console.log(cart)

  return (
    <Router>
      <div>
        <Navbar onAdd={onAdd} cartItems={JSON.parse(localStorage.getItem("cartItems")) } />
        <Switch>
          <Route exact path="/">
            <Products onAdd={onAdd} products={products} />

          </Route>

          <Route exact path="/cart">
            <Cart onAdd={onAdd} cartItems={JSON.parse(localStorage.getItem("cartItems"))} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
